mapper.select:
select count(*) 
	from camping c
	join camping_img img
	on c.camping_idx = img.camping_idx
	where img.first_img is not null
		
			and doNm = #{firstSelect} 
		
		
			and sigunguNm = #{secondSelect} 
		
		
			and lctcl = #{details} 
					
		
			and facltnm like '%${keyword}%'
		
	order by c.camping_idx desc

select c.*, img.first_img from camping c join camping_img img on c.camping_idx = img.camping_idx where img.first_img is not null
and doNm = #{firstSelect}
and sigunguNm = #{secondSelect}
and lctcl = #{details}
and facltnm like '%${keyword}%'
order by c.camping_idx desc offset #{paging.offset} rows fetch next #{paging.perPage} rows only
